MyString.h
#ifndef __MYSTRING_H_ #define __MYSTRING_H_  #include &lt;stdlib.h&gt;#include &lt;iostream&gt;#include &lt;cstring&gt; using namespace std; class MyString { // 重载 &lt;&lt;  // 最好是 const 的，可以输出匿名变量（匿名变量不能被非 const 的左值引用接收）  friend ostream&amp; operator&lt;&lt;(ostream &amp;os, const MyString &amp;s); // 重载 &gt;&gt;  friend istream&amp; operator&gt;&gt;(istream &amp;is, MyString &amp;s); private: // 字符串的有效长度  int m_len; // 字符串指针  char* m_pStr; public: MyString(); MyString(const char *); MyString(const MyString &amp;); ~MyString(); // 重载 =  MyString&amp; operator=(const MyString &amp;); // 重载 []  char&amp; operator[](int index); // 重载 +  MyString operator+(const MyString &amp;); // 重载 +=  MyString&amp; operator+=(const MyString &amp;); // 重载 ==  bool operator==(const MyString &amp;); // 重载 !=  bool operator!=(const MyString &amp;); }; #endif MyString.cpp
#include &#34;MyString.h&#34; ostream&amp; operator&lt;&lt;(ostream &amp;os, const MyString &amp;s){ os &lt;&lt; s.m_pStr; return os; } istream&amp; operator&gt;&gt;(istream &amp;is, MyString &amp;s){ char temp[4096] = {0}; is &gt;&gt; temp; if (s.m_pStr != NULL) { s.m_len = 0; free(s.m_pStr); s.m_pStr = NULL; } s.m_len = strlen(temp); s.m_pStr = (char*)malloc(s.m_len); strcpy(s.m_pStr, temp); return is; } MyString::MyString() { m_len = 0; m_pStr = NULL; // 注意 &#39;\0&#39; NULL &#34;&#34;的区别 } MyString::MyString(const char *c) { if (c != NULL) { m_len = strlen(c); m_pStr = (char*)malloc(m_len + 1); strcpy(m_pStr, c); } cout &lt;&lt; &#34;MyString(const char *c)...&#34; &lt;&lt; endl; } MyString::MyString(const MyString &amp;s) { if (s.m_pStr != NULL) { m_len = s.m_len; m_pStr = (char*)malloc(m_len + 1); strcpy(m_pStr, s.m_pStr); } cout &lt;&lt; &#34;MyString(const MyString &amp;s)...&#34; &lt;&lt; endl; } MyString::~MyString() { m_len = 0; if (m_pStr != NULL) { free(m_pStr); m_pStr = NULL; } cout &lt;&lt; &#34;~MyString()...&#34; &lt;&lt; endl; } MyString&amp; MyString::operator=(const MyString &amp;s) { cout &lt;&lt; &#34;operator=(const MyString &amp;s)...&#34; &lt;&lt; endl; // 1. 是否是给自身赋值  if (this == &amp;s) { return *this; } // 2. 处理自身垃圾  if (m_pStr != NULL) { free(m_pStr); m_pStr = NULL; } // 3. 深拷贝  if (s.m_pStr != NULL) { m_len = s.m_len; m_pStr = (char*)malloc(m_len + 1); strcpy(m_pStr, s.m_pStr); } // 4. 返回自身  return *this; } char&amp; MyString::operator[](int index) { if (m_len == 0) { throw &#34;bad memory&#34;; } if (index &lt; 0 || index &gt;= m_len) { throw &#34;out of range&#34;; } return m_pStr[index]; } MyString MyString::operator+(const MyString &amp;s) { // 方式一：  /* MyString temp; temp.m_len = m_len + s.m_len; temp.m_pStr = (char*)malloc(temp.m_len + 1); // memset(temp.m_pStr, 0, temp.m_len + 1); strcpy(temp.m_pStr, m_pStr); // 注意 strcpy 和 strcat 的使用 strcat(temp.m_pStr, s.m_pStr); // 注意 strcat 的使用 return temp; */ // 方式二：  MyString temp = *this; return temp += s; } MyString&amp; MyString::operator+=(const MyString &amp;s) { if (s.m_len &gt; 0) { // 处理自身垃圾  if (m_pStr != NULL) { char* pTemp = m_pStr; m_len += s.m_len; m_pStr = (char*)malloc(m_len + 1); strcpy(m_pStr, pTemp); strcat(m_pStr, s.m_pStr); free(pTemp); pTemp = NULL; } } return *this; } bool MyString::operator==(const MyString &amp;s) { if (m_len == 0 || s.m_len == 0) { return false; } if(m_len != s.m_len) { return false; } for (int i = 0; i &lt; m_len; i++) { if (m_pStr[i] != s.m_pStr[i]) { return false; } } return true; } bool MyString::operator!=(const MyString &amp;s) { return !(*this == s); } test.cpp
#include &lt;iostream&gt;#include &#34;MyString.h&#34; using namespace std; int main(){ // 隐式转换  MyString s0 = &#34;123&#34;; cout &lt;&lt; s0 &lt;&lt; endl; // 构造函数  MyString s1(&#34;abc&#34;); cout &lt;&lt; s1 &lt;&lt; endl; // 拷贝构造  MyString s2 = s1; cout &lt;&lt; s2 &lt;&lt; endl; // 拷贝构造  MyString s3(s2); cout &lt;&lt; s3 &lt;&lt; endl; // = 操作符  s3 = s1; cout &lt;&lt; s3 &lt;&lt; endl; // [] 操作符  cout &lt;&lt; s3[1] &lt;&lt; endl; s3[1] = &#39;1&#39;; cout &lt;&lt; s3 &lt;&lt; endl; // + 操作符  cout &lt;&lt; s1 + s2 +s3 &lt;&lt; endl; // += 操作符  s3 += s1; cout &lt;&lt; s3 &lt;&lt; endl; // == 操作符  cout &lt;&lt; (s3 == s1) &lt;&lt; endl; // != 操作符  cout &lt;&lt; (s3 != s1) &lt;&lt; endl; return 0; } 输出：
MyString(const char *c)... abc MyString(const MyString &amp;s)... abc MyString(const MyString &amp;s)... abc operator=(const MyString &amp;s)... abc b a1c abcabca1c ~MyString()... ~MyString()... a1cabc 0 1 ~MyString()... ~MyString()... ~MyString()...

买卖货物问题：
#include &lt;iostream&gt; using namespace std; class Goods{ public: Goods(){ m_pNext = nullptr; m_weight = 0; cout &lt;&lt; &#34;买入了货物，重量是：&#34; &lt;&lt; m_weight &lt;&lt; endl; } Goods(int weight){ m_pNext = nullptr; m_weight = weight; total_weight += m_weight; cout &lt;&lt; &#34;买入了货物，重量是：&#34; &lt;&lt; m_weight &lt;&lt; endl; } ~Goods(){ total_weight -= m_weight; // m_pNext 不是在本类中 new 的，所以也不用 delete  this-&gt;m_pNext = nullptr; cout &lt;&lt; &#34;卖出了货物，重量是：&#34; &lt;&lt; m_weight &lt;&lt; endl; } static int getTotalWeight(){ return total_weight; } Goods *m_pNext; private: int m_weight; static int total_weight; }; int Goods::total_weight = 0; void buy(Goods *&amp;head, int weight){ Goods *pNewGoods = new Goods(weight); if(head == nullptr){ head = pNewGoods; } else{ pNewGoods-&gt;m_pNext = head; head = pNewGoods; } } void sale(Goods *&amp;head){ if(head == nullptr){ cout &lt;&lt; &#34;没有货物了&#34; &lt;&lt; endl; return; } Goods *temp = head; head = head-&gt;m_pNext; delete temp; temp = nullptr; } int main( ) { Goods *head = nullptr; int choice; do{ // 提供菜单  cout &lt;&lt; &#34;输入 1 进货&#34; &lt;&lt; endl; cout &lt;&lt; &#34;输入 2 出货&#34; &lt;&lt; endl; cout &lt;&lt; &#34;输入 0 退出&#34; &lt;&lt; endl; cin &gt;&gt; choice; switch(choice){ case 0: // 退出  return 0; case 1: { // 进货  int w = 0; cout &lt;&lt; &#34;输入货物重量：&#34; &lt;&lt; endl; cin &gt;&gt; w; buy(head, w); break; } case 2: // 出货  sale(head); break; } cout &lt;&lt; &#34;目前货物的总重量是：&#34; &lt;&lt; Goods::getTotalWeight() &lt;&lt; endl; }while(1); return 0; } 输出：
输入 1 进货 输入 2 出货 输入 0 退出 1 输入货物重量： 10 买入了货物，重量是：10 目前货物的总重量是：10 输入 1 进货 输入 2 出货 输入 0 退出 1 输入货物重量： 20 买入了货物，重量是：20 目前货物的总重量是：30 输入 1 进货 输入 2 出货 输入 0 退出 1 输入货物重量： 30 买入了货物，重量是：30 目前货物的总重量是：60 输入 1 进货 输入 2 出货 输入 0 退出 2 卖出了货物，重量是：30 目前货物的总重量是：30 输入 1 进货 输入 2 出货 输入 0 退出 2 卖出了货物，重量是：20 目前货物的总重量是：10 输入 1 进货 输入 2 出货 输入 0 退出 2 卖出了货物，重量是：10 目前货物的总重量是：0 输入 1 进货 输入 2 出货 输入 0 退出 2 没有货物了 目前货物的总重量是：0 输入 1 进货 输入 2 出货 输入 0 退出 0

 父类指针可以new子类对象，子类指针不可以new父类对象，但是可以接收强转的已经存在的父类指针 父类指针指向本类对象，调用的虚函数和普通函数都是自己的 将子类指针指向父类对象，虚函数执行的是父类的，也可以调用子类函数（这种操作是错误的，编译会报错，虽然可通过指针强转，但是不建议这样做，可能会导致未知错误） 父类指针指向子类对象，虚函数执行的是子类的，并且不能调用子类的函数 父类指针与子类指针之间赋值可以理解为指针所指内容的一种浅拷贝
Father *f = (Father*)Child * 子类的虚函数指针覆盖父类的，并且子类特有的函数指针不会拷贝过去
Child *c = (Child*)new Father父类的虚函数指针覆盖子类的，同时也有子类特有的函数指针  class A{ public: void printA(){ cout &lt;&lt; &#34;printA()&#34; &lt;&lt; endl; } virtual void print(){ cout &lt;&lt; &#34;A::print()&#34; &lt;&lt; endl; } }; class B:public A{ public: void printB(){ cout &lt;&lt; &#34;printB()&#34; &lt;&lt; endl; } virtual void print() override{ cout &lt;&lt; &#34;B::print() b = &#34; &lt;&lt; b &lt;&lt; endl; } private: int b; }; int main( ) { // 1、指向自己的类对象（将父类对象赋值给父类指针）  A *a = new A; // 1.1 调用自己的函数  a-&gt;printA(); // printA()  // 1.2 调用虚函数  a-&gt;print(); // A::print()  // 2、将指向父类的父类指针强转为子类指针（将父类对象赋值给子类指针）  // b = new A; // error: incompatible pointer types assigning to &#39;B *&#39; from &#39;A *&#39;  B *b = (B*)new A; // 2.1 调用父类函数  b-&gt;printA(); // printA()  // 2.2 调用虚函数（执行父类的）  b-&gt;print(); // A::print() 调用父类的  // 2.3 可以调用子类函数  b-&gt;printB(); // printB() b = 0  // 3、将子类对象赋值给父类指针  a = new B; // 3.1 调用父类函数  a-&gt;printA(); // printA()  // 3.2 调用子类的函数  // a-&gt;printB(); // error: no member named &#39;printB&#39; in &#39;A&#39;; did you mean &#39;printA&#39;?  // 3.3 调用虚函数（执行子类的）  a-&gt;print(); // B::print() b = 0  // 4、指向子类对象的子类指针强转为父类指针（效果等同情况3）  b = new B; a = (A*)b; a-&gt;printA(); // printA()  //a-&gt;printB(); // error: no member named &#39;printB&#39; in &#39;A&#39;  a-&gt;print(); // B::print() b = 0  return 0; }

summary:
1. 函数默认参数和占位参数（亚元） 形参中的占位参数又叫亚元，并没有什么实际意义，只在函数重载中起到作用
// 亚元，设置了默认值所以调用时可以不传参数 // 否则就必须传入两个参数 void foo(int a, int = 0){ cout &lt;&lt; &#34;a = &#34; &lt;&lt; a &lt;&lt; endl; } int main() { foo(20); return 0; } 2. 函数重载  函数名相同，形参列表不同（形参个数、类型、顺序） 函数返回值不起作用 函数重载和默认参数不要同时使用（函数调用时容易产生二义性） 倾轧技术（name mangling），底层会将函数名进行编译  用 v c i f l d 表示 void char int float long double 及其引用 int fun(int) =&gt; fun_i
int fun(int, char, double) =&gt; fun_icd   重载函数匹配顺序  如果能够严格匹配，则调用完全匹配的 如果没有完全匹配的，则调用隐式转换的 都匹配不上，编译失败    3.

